package com.techlink.leetcode;

public class PalindromeII {

    // TODO: to be completed.
    public static boolean validPalindrome(String s) {
        StringBuilder stringBuilder = new StringBuilder(s.toLowerCase());

        if(stringBuilder.toString().equals(stringBuilder.reverse().toString())) return true;

        for(int i=0; i<stringBuilder.length(); i++) {
            StringBuilder removed = new StringBuilder(stringBuilder.toString());

            removed.deleteCharAt(i);

            if(removed.toString().equals(removed.reverse().toString())) {
                return true;
            }
        }

        return false;
    }



    public static void main(String[] args) {
        System.out.println(validPalindrome("abc"));
        System.out.println(validPalindrome("abca"));
        System.out.println(validPalindrome(""));
    }

}
